(2, 'Admin', 'info@satukoneksi.com', '1. sldld\n2.dasd', '2015-05-28', 'Admin', 1),
(3, 'Amal Udjir', 'amaludjir@gmail.com', 'Oke Terimakasih ', '2015-05-28', 'Guest', 1),
(4, 'Rocky', 'amaludjir@gmail.com', 'Yo opo iku?', '2015-06-09', 'Guest', 2),
(5, 'Henividia', 'heni_vidia@yahoo.com', 'menurut Anda hal apa yang harus dilakukan untuk memulai suatu bisnis?', '2015-06-17', 'Guest', 3),
(6, 'Henividia', 'heni_vidia@yahoo.com', 'Bagaimana anda mendapatkan modal awal untuk memulai suatu bisnis?', '2015-06-17', 'Guest', 4),
(7, 'Henividia', 'heni_vidia@yahoo.com', 'Bagaimanakah ruang lingkup dari hukum berbisnis?', '2015-06-17', 'Guest', 5),
(8, 'Admin', 'info@satukoneksi.com', '<div>\n	Adapun yang merupakan ruang lingkup dari hukum bisnis ini, antara lain adalah sebagai berikut :</div>\n<div>\n	1. Kontrak Bisnis</div>\n<div>\n	2. Jual beli</div>\n<div>\n	3. Bentuk-bentuk perusahaan</div>\n<div>\n	4. Perusahaan go public dan pasar modal</div>\n<div>\n	5. Penanaman modal asing</div>\n<div>\n	6. Kepailitan dan likuidasi</div>\n<div>\n	7. Merger dan akuisisi</div>\n<div>\n	8. Perkreditan dan pembiayaan</div>\n<div>\n	9. Jaminan hutang</div>\n<div>\n	10. Surat berharga</div>\n<div>\n	11. Perburuhan</div>\n<div>\n	12. Hak atas kekayaan intelektual</div>\n<div>\n	13. Anti monopoli</div>\n<div>\n	14. Perlindungan konsumen</div>\n<div>\n	15. Keagenan dan distribusi</div>\n<div>\n	16. Asuransi</div>\n<div>\n	17. Perpajakan</div>\n<div>\n	18. Penyelesaian sengketa bisnis</div>\n<div>\n	19. Bisnis internasional</div>\n<div>\n	20. Hukum pengangkutan (darat, laut, udara, dan multimodal)</div>\n', '2015-06-17', 'Admin', 5),
(9, 'Admin', 'info@satukoneksi.com', '<p>\n	Modal awal dapat diperoleh dari:</p>\n<p>\n	1. modal sendiri</p>\n<p>\n	2. partnersihp</p>\n<p>\n	3. pinjaman bank</p>\n<p>\n	4. investor</p>\n<p>\n	5. lomba</p>\n<p>\n	6. crowdfunding</p>\n<p>\n	&nbsp;</p>\n', '2015-06-17', 'Admin', 4),
(10, 'Admin', 'info@satukoneksi.com', '<p>\n	hal-hal yang harus diperhatikan saat akan memulai suatu bisnis adalah:</p>\n<p>\n	- merencanakan dengan matang, namun tetap fleksible</p>\n<p>\n	- kenalilah produk dan pasar</p>\n<p>\n	- jadilah pembisnis yang unik</p>\n<p>\n	- membentuk tim sukses</p>\n<p>\n	- trik pemasaran produk</p>\n<p>\n	- membuat visi dan misi bisnis</p>\n<p>\n	- memiliki pengetahuan dasar manajemen, organisasi, dan sistem</p>\n', '2015-06-17', 'Admin', 3),
(11, 'Henividia', 'heni_vidia@yahoo.com', 'terimakasih', '2015-06-17', 'Guest', 3);

